import { gql, useMutation } from '@apollo/client';
import FormStyles from './styles/Form';
import useForm from '../lib/useForm';
import { CURRENT_USER_QUERY } from './User';
import DisplayError from './ErrorMessage';

const RESET_MUTATION = gql`
  mutation RESET_MUTATION(
    $email: String!
    $password: String!
    $token: String!
  ) {
    redeemUserPasswordResetToken(
      email: $email
      password: $password
      token: $token
    ) {
      code
      message
    }
  }
`;

export default function Reset() {
  const { inputs, handleChange, resetForm } = useForm({
    email: '',
    password: '',
    token: '',
  });

  const [resetPassword, { data, loading }] = useMutation(RESET_MUTATION, {
    variables: inputs,
  });

  async function handleSubmit(e) {
    e.preventDefault();
    console.log(inputs);
    const res = await resetPassword().catch(console.error);
    console.dir(res);
  }

  if (loading) return <p>Loading...</p>;
  if (data?.redeemUserPasswordResetToken === null) {
    return <p>Success! You can now sign in with your new password.</p>;
  }

  return (
    <FormStyles method="POST" onSubmit={handleSubmit}>
